body.script:
const c = document.getElementById("game");
const ctx = c.getContext("2d");

c.width = 600;
c.height = 400;

let x = 0;

function loop() {
  ctx.clearRect(0, 0, c.width, c.height);
  ctx.fillStyle = "lime";
  ctx.fillRect(x, 180, 50, 50);
  x += 2;
  requestAnimationFrame(loop);
}

loop();
